	/**
	 * para excluir campos pasar como parametro hide
	 * los campos, ejemplo: ?hide[]=created_at&hide[]=updated_at
	 * para mostrar solo campos especificos pasar como parametros
	 * show con los campos, ejemplo ?show[]=username&show[]=typeuser
	 **/
	public function index($table, Request $request){
		$db = DB::table($table)->get();

		foreach ($db as $key => $item) {
			$item->id = $key+1;
			if ( isset($item->password) ){
				unset( $item->password );
			}
			if ( isset($request->hide) ){
				foreach ($request->hide as $campo){
					if (isset( $item->$campo )){
						unset( $item->$campo );
					}
				}
			}
			if ( isset($request->show) ){
				$dbTemp[$key]['id'] = $item->id;
				foreach ($request->show as $campo){
					if (isset( $item->$campo )){
						$dbTemp[$key][$campo] =$item->$campo;
					}
				}
			}
		}

		if (isset($dbTemp)){
			return JsonResponse::create($dbTemp);
		}else{
			return JsonResponse::create($db);
		}
	}

	public function input($table, Request $request){
		$db = DB::table($table)->select($request->input)->where($request->input, $request->search)->get();
		return JsonResponse::create($db);
	}
	public function inputs($table, Request $request){
		//$db = DB::table($table)->select($request->input)->get();
		$db = DB::table($table)->selectRaw($request->input.' as resultado')->get();
		return JsonResponse::create($db);
		//echo 'hola mundo';
	}

